mapper.select:

SELECT * FROM portal_limit



    AND code = #{code}


    AND name LIKE CONCAT('%', #{name}, '%')


    AND status = #{status}



OFFSET (#{page}-1) * #{limit} ROWS
FETCH NEXT #{limit} ROWS ONLY


SELECT COUNT(*) FROM portal_limit



    AND code = #{code}


    AND name LIKE CONCAT('%', #{name}, '%')


    AND status = #{status}
